// 手机验证码倒计时
function Countdown(obj,input_obj,classname,num){
	$(obj).attr("value","1");
	var jte=num;
	var timer=null;
	function miuete(){
			jte--;
			if(jte<=0) {
			$(obj).removeClass(classname).text('重新获取');
				jte=num;
				clearInterval(timer);
				$(obj).attr("value","1");
			}
			$(obj+'.'+classname).text(jte+'s');
		}
	$(obj).click(function(){
		if($(input_obj).val()!=''){
			if($(this).attr("value")=="1"){
				$(this).attr("value","0");
					$(this).addClass(classname);
					miuete();
					timer=setInterval(miuete,1000);
			}
		}

	})
}


